    ensures
        seq.slice_wf(),
        seq.spec_len() == data@.len(),
        forall|i: int| #![trigger seq.spec_index(i)]
            0 <= i < data@.len() ==> seq.spec_index(i) == data@[i];

fn empty() -> (empty_seq: Self)
    ensures
        empty_seq.slice_wf(),
        empty_seq.spec_len() == 0;

