fn leaf() -> (l: Self)
    ensures l.spec_size() == 0,
            l.spec_height() == 0,
            l.spec_in_order() == Seq::<T>::empty(),
            l.spec_pre_order() == Seq::<T>::empty(),
            l.spec_post_order() == Seq::<T>::empty();

fn size(&self) -> (count: usize)
    requires self.spec_size() <= usize::MAX,
    ensures count == self.spec_size();

fn height(&self) -> (h: usize)
    requires self.spec_height() <= usize::MAX,
    ensures h == self.spec_height();

fn in_order(&self) -> (traversal: Vec<T>)
    where T: Clone + Eq
    requires self.spec_size() <= usize::MAX,
             obeys_feq_clone::<T>(),
    ensures traversal@ =~= self.spec_in_order();

fn pre_order(&self) -> (traversal: Vec<T>)
    where T: Clone + Eq
    requires self.spec_size() <= usize::MAX,
             obeys_feq_clone::<T>(),
    ensures traversal@ =~= self.spec_pre_order();

fn post_order(&self) -> (traversal: Vec<T>)
    where T: Clone + Eq
    requires self.spec_size() <= usize::MAX,
             obeys_feq_clone::<T>(),
    ensures traversal@ =~= self.spec_post_order();

fn next(&mut self) -> (next: Option<T>)
    ensures ({
        let (old_index, old_seq) = old(self)@;
        match next {
            None => {
                &&& self@ == old(self)@
                &&& old_index >= old_seq.len()
            },
            Some(element) => {
                let (new_index, new_seq) = self@;
                &&& 0 <= old_index < old_seq.len()
                &&& new_seq == old_seq
                &&& new_index == old_index + 1
                &&& element == old_seq[old_index]
            },
        }
    })

fn eq(&self, other: &Self) -> (equal: bool)
    ensures equal == (*self == *other)
    decreases self,

fn clone_tree<T: Clone>(t: &BalBinTree<T>) -> (c: BalBinTree<T>)
    ensures c == *t
    decreases t,

fn singleton(value: T) -> (single: Self)
    ensures
        single.spec_len() == 1,
        single.spec_index(0) == value;

fn from_vec(vec: Vec<T>) -> (seq: Self)
    ensures
        seq.spec_len() == vec@.len(),
        forall|i: int| #![trigger seq.spec_index(i)] 0 <= i < vec@.len() ==> seq.spec_index(i) == vec@[i];

fn nth(&self, index: usize) -> (nth_elem: &T)
    requires index < self.spec_len()
    ensures *nth_elem == self.spec_index(index as int);

fn join(tree: PrimTreeSeqStTree<T>) -> (joined: Self)
    ensures
        tree@ is Zero ==> joined@ =~= Seq::<T>::empty(),
        tree@ is One ==> joined@ =~= seq![tree@->One_0],
        tree@ is Two ==> joined@ =~= tree@->Two_0 + tree@->Two_1;

fn map<U: Clone, F: Fn(&T) -> U>(a: &PrimTreeSeqStS<T>, f: &F) -> (mapped: PrimTreeSeqStS<U>)
    requires
        forall|i: int| 0 <= i < a.spec_len() ==> #[trigger] f.requires((&a.spec_index(i),)),
    ensures
        mapped.spec_len() == a.spec_len(),
        forall|i: int| #![trigger mapped.spec_index(i)] 0 <= i < a.spec_len() ==> f.ensures((&a.spec_index(i),), mapped.spec_index(i));

fn flatten(a: &PrimTreeSeqStS<PrimTreeSeqStS<T>>) -> (flattened: PrimTreeSeqStS<T>)
    where T: Clone + Eq
    requires
        obeys_feq_clone::<T>(),
    ensures
        flattened.seq@ =~= a.seq@.map_values(|inner: PrimTreeSeqStS<T>| inner.seq@).flatten();

