fn select(a: &ArraySeqMtEphS<T>, k: usize) -> (found: Option<T>)
    requires a.spec_len() <= usize::MAX,
    ensures
        k >= a.spec_len() ==> found is None,
        k < a.spec_len() ==> found == Some(spec_kth::<T>(
            Seq::new(a.spec_len(), |i: int| a.spec_index(i)), k as int));

